// TODO ls | cat << EOF
// pipeに入る前にheredocを全部読み込む

// TODO heredocの時、終端文字列は変数展開しない
// bash-3.2$ AAA=123
// bash-3.2$ BBB=456
// bash-3.2$ cat << $BBB
// > $AAA # <-これは変数展開する
// > $BBB # <-これは変数展開せず、終端文字列とする
// 123

// cat << '' # 空行を終端とする
// cat << "'" # 'を終端とする
// cat << '"' # "を終端とする
// 終端文字に',"が含まれている場合は変数展開しない

// 17個の場合、bash: maximum here-document count exceededでbashごと落ちる
// cat << EOF << EOF << EOF << EOF << EOF << EOF << EOF << EOF << EOF << EOF << EOF << EOF << EOF << EOF << EOF << EOF << EOF
// パイプがあっても、16個を超えると同様にエラーで落ちる
// cat << EOF << EOF << EOF << EOF << EOF << EOF << EOF << EOF | cat << EOF << EOF << EOF << EOF << EOF << EOF << EOF << EOF << EOF

void	init_heredoc(t_heredoc *heredoc)
{
	int	i;

	heredoc->num = 0;
	i = 0;
	while (i < 16)
	{
		heredoc->delimiters[i] = NULL;
		heredoc->docs[i] = NULL;
		i++;
	}
}

void	free_heredoc(t_heredoc *heredoc)
{
	int	i;

	i = 0;
	while (i < 16)
	{
		if (heredoc->delimiters[i])
			free(heredoc->delimiters[i]);
		if (heredoc->docs[i])
			free(heredoc->docs[i]);
		i++;
	}
}

int	set_heredoc(t_minishell *minish, t_token *tok)
{
	int	idx;

	idx = minish->heredoc.num;
	minish->heredoc.num++;
	// TODO heredocの場合、"、'は展開するが変数展開しないのでexpandのロジックを変える
	minish->heredoc.delimiters[idx] = expand(minish, tok);
	minish->heredoc.need_expansion[idx] = (ft_memchr(tok->str, '\'',
				tok->len) == NULL) && (ft_memchr(tok->str, '\"',
				tok->len) == NULL);
	return (idx);
}

void	heredoc2(void)
{
	int		fds[2];
	pid_t	pid;

	char buf[] = "999999999999999999";
	if (pipe(fds) < 0)
	{
		perror("pipe");
		exit(EXIT_FAILURE);
	}
	pid = fork();
	if (pid < 0)
	{
		perror("fork");
		exit(EXIT_FAILURE);
	}
	if (pid == 0)
	{
		// 子プロセス
		close(fds[0]);
		write(fds[1], buf, ft_strlen(buf));
		close(fds[1]);
		exit(EXIT_SUCCESS);
	}
	// 親プロセス
	close(fds[1]);
	dup2(fds[0], STDIN_FILENO);
	close(fds[0]);
	// waitpid(pid, &status, 0);
}

